                                <?php
                                function get_word_count($content)
                                {
                                    $content = preg_replace('/\s+/', ' ', strip_tags($content)); // حذف تگ‌ها و اضافی‌های فاصله
                                    $words = preg_split('/\s/', $content, -1, PREG_SPLIT_NO_EMPTY);
                                    return round(count($words) / 200, 0, PHP_ROUND_HALF_UP);

                                }
                                $post_word_count = get_word_count(get_the_content());

