                <?php
                // ۱) سعی می‌کنیم دسته‌های سطح اول (parent=0) که حداقل یک زیرمجموعه دارند را پیدا کنیم
                $top_level = get_terms([
                    'taxonomy' => 'product_cat',
                    'hide_empty' => false,
                    'parent' => 0,
                ]);

                $main_with_children = [];
                foreach ($top_level as $cat) {
                    // با یک کوئری کوتاه چک می‌کنیم که آیا فرزندی دارد یا نه
                    $has_child = get_terms([
                        'taxonomy' => 'product_cat',
                        'hide_empty' => false,
                        'parent' => $cat->term_id,
                        'number' => 1,      // فقط برای بررسی وجود حداقل یک فرزند
                    ]);
                    if (!empty($has_child)) {
                        $main_with_children[] = $cat;
                        if (count($main_with_children) >= 7) {
                            break;
                        }
                    }
                }

                // ۲) اگر هیچ دستهٔ اصلی با زیرمجموعه پیدا نشد، ۷ تا از دسته‌های معمولی را بگیرید
                if (!empty($main_with_children)) {
                    $categories = $main_with_children;
                } else {
                    $categories = get_terms([
                        'taxonomy' => 'product_cat',
                        'hide_empty' => false,
                        'number' => 7,
                    ]);
                }

                if (!empty($categories)): ?>
